class Prices extends React.Component {
  state = {
    currency: 'USD'
  }

  render() {
    return (
    <div className={'flex flex-row'}>
        <h2 className={'sm:text-lg p-4 text-blue-700'}> Bitcoin rate for: {this.props.bpi.bpi.USD.description}</h2>
        <ul>
          <li className={'sm:text-lg p-4 text-sm  text-blue-700'}><span className={'text-white-700 bg-red-400'}>{this.props.bpi.bpi.USD.code}
            {' '} {this.props.bpi.bpi.USD.rate}</span>
          </li>
        </ul>
    </div>);
  }
}

export default Prices;
